mapper.select:
SELECT ar.*, c.c_id AS cId, c.c_name AS cName, h.h_id AS hId, h.h_name AS hName, cl.cl_id AS clId, cl.cl_u_id AS clUId, a.a_id AS aId, a.a_time AS aTime FROM arrive ar LEFT JOIN appointment a ON ar.ar_a_id=a.a_id LEFT JOIN hospital h ON h.h_id=a.a_h_id LEFT JOIN clue cl ON cl.cl_id=a.a_cl_id LEFT JOIN customer c ON c.c_id=cl.cl_c_id
AND cl.cl_u_id = #{uId}
AND c.c_name LIKE '%${cName}%'
AND cl.cl_invalid = 0 AND (a.a_invalid = 0 OR a.a_invalid is NULL) AND ar.ar_invalid = 0
limit #{page}, #{limit}
