mapper.update:
UPDATE clue SET cl_project = #{clProject}, cl_earnest = #{clEarnest},
cl_u_id = #{clUId}, cl_source = #{clSource}, cl_remark = #{clRemark},
cl_message = #{clMessage}, cl_type_id = #{clTypeId}
WHERE cl_c_id = #{clCId}

mapper.select:
SELECT cl.*, c.c_name AS cName FROM clue cl
LEFT JOIN customer c ON c.c_id=cl.cl_c_id
WHERE cl.cl_id = #{clId}
