mapper.select:
SELECT fl.*, c.c_id AS cId, c.c_name AS cName, h.h_id AS hId, h.h_name AS hName, cl.cl_id AS clId, cl.cl_u_id AS clUId FROM fail fl LEFT JOIN appointment a ON a.a_id=fl.fl_a_id LEFT JOIN clue cl ON cl.cl_id=a.a_cl_id LEFT JOIN customer c ON c.c_id=cl.cl_c_id LEFT JOIN hospital h ON fl.fl_h_id=h.h_id
AND cl.cl_u_id = #{uId}
AND c.c_name LIKE '%${cName}%'
AND cl.cl_invalid = 0 AND a.a_invalid = 1 AND fl.fl_invalid = 0
limit #{page}, #{limit}

SELECT COUNT(*) FROM fail fl LEFT JOIN appointment a ON a.a_id=fl.fl_a_id LEFT JOIN clue cl ON cl.cl_id=a.a_cl_id LEFT JOIN customer c ON c.c_id=cl.cl_c_id LEFT JOIN hospital h ON fl.fl_h_id=h.h_id
AND cl.cl_u_id = #{uId}
AND c.c_name = #{cName}
AND cl.cl_invalid = 0 AND a.a_invalid = 1 AND fl.fl_invalid = 0
