mapper.select:
SELECT r.*, c.c_id AS cId, c.c_name AS cName, h.h_id AS hId, ho.h_name AS failHName, h.h_name AS hName FROM referral r LEFT JOIN appointment a ON a.a_id=r.r_a_id LEFT JOIN clue cl ON cl.cl_id=a.a_cl_id LEFT JOIN customer c ON c.c_id=cl.cl_c_id LEFT JOIN hospital h ON r.r_h_id=h.h_id LEFT JOIN hospital ho ON r.r_fail_h_id=ho.h_id
AND cl.cl_u_id = #{uId}
AND c.c_name LIKE '%${cName}%'
limit #{page}, #{limit}

SELECT COUNT(*) FROM referral r LEFT JOIN appointment a ON a.a_id=r.r_a_id LEFT JOIN clue cl ON cl.cl_id=a.a_cl_id LEFT JOIN customer c ON c.c_id=cl.cl_c_id LEFT JOIN hospital h ON r.r_h_id=h.h_id LEFT JOIN hospital ho ON r.r_fail_h_id=ho.h_id
AND cl.cl_u_id = #{uId}
AND c.c_name = #{cName}
