mapper.select:
SELECT s.*, c.c_id AS cId, c.c_name AS cName, h.h_id AS hId, u.u_name AS uName, h.h_name AS hName, cl.cl_id AS clId, cl.cl_u_id AS clUId FROM success s LEFT JOIN appointment a ON a.a_id=s.s_a_id LEFT JOIN clue cl ON cl.cl_id=a.a_cl_id LEFT JOIN customer c ON c.c_id=cl.cl_c_id LEFT JOIN hospital h ON s.s_h_id=h.h_id LEFT JOIN `user` u ON u.u_id=cl.cl_u_id
AND cl.cl_u_id = #{uId}
AND c.c_name LIKE '%${cName}%'
AND cl.cl_invalid = 0 AND a.a_status = 2
ORDER BY s.s_time DESC limit #{page}, #{limit}
