mapper.select:
SELECT s.*, c.c_id AS cId, c.c_name AS cName, h.h_id AS hId,
h.h_name AS hName FROM success s
LEFT JOIN customer c ON s.s_c_id=c.c_id
LEFT JOIN hospital h ON s.s_h_id=h.h_id


    AND c.c_u_id = #{uId}


    AND c.c_name = #{cName}


limit #{page}, #{limit}

SELECT COUNT(*) FROM success s
LEFT JOIN customer c ON s.s_c_id=c.c_id
LEFT JOIN hospital h ON s.s_h_id=h.h_id


    AND c.c_u_id = #{uId}


    AND c.c_name = #{cName}
